<?php

namespace Kunstmaan\MediaBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * GalleryRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class VideoGalleryRepository extends EntityRepository
{

    public function getAllGalleries($limit = null)
    {
            $qb = $this->createQueryBuilder('videogallery')
                       ->select('videogallery')
                       ->where('videogallery.parent is null');

            if (false === is_null($limit))
                $qb->setMaxResults($limit);

            return $qb->getQuery()
                      ->getResult();
    }
}